import { useEffect, useState } from 'react';
import {jwtDecode} from 'jwt-decode';

function useUser() {
  const [user, setUser] = useState('');

  useEffect(() => {
    let authToken = window.localStorage.getItem("accessToken");
    let user = jwtDecode(authToken);
    if (user) {
      setUser(user);
    }
  }, []);

  return user;
}

export default useUser;
